const path = require('path');
const cheerio = require('cheerio');

module.exports = function createTemps(file) {
  const html = file.contents.toString();
  const fileName = path.basename(file.path, '.html');
  const folderName = path.dirname(file.path).split(path.sep).pop();
  const newHtml = html.match(/<div class="mxgraph".*<\/div>/)[0];
  const $ = cheerio.load(newHtml);
  $('.mxgraph').attr('id', fileName);
  return $.html();
}